sys_script_include.script:
var budgeting = Class.create();
budgeting.prototype = {
	initialize: function() {
	},
	//here we mint new ITC, usually based on the total budget, because we will allocate that later to different boc's
	//the idea is this is the total ITC for the year, all variance reporting will be based on that fact

	//TODO
	/*
	Amount	Integer
	Expiry date	Date
	Label	String
	Mint date	Date
	Owner	Reference	User
	Decimals	Integer
	Symbol	String
 	*/

	mintItc: function(_name, _amount) {

		var minted_gr = new GlideRecord("x_83654_itcash_minted_itc");
		minted_gr.initialize();
		minted_gr.label = _name;
		minted_gr.amount = _amount;
		minted_gr.insert();
	},

	allocateItc: function(_name, _amount) {
		var minted_gr = new GlideRecord("x_83654_itcash_minted_itc");
		minted_gr.initialize();
		minted_gr.label = _name;
		minted_gr.amount = _amount;
		minted_gr.insert();
	},
	type: 'budgeting'
};
